/* ClaudDib Website Styles */

:root {
    --desert-sand: #f5f1e8;
    --desert-shadow: #2d2a26;
    --desert-accent: #c9a227;
    --desert-muted: #8b8378;
    --desert-border: #d4cfc4;
    --crustacean: #d4574a;
    --text-body: #3d3a36;
    --text-heading: #1a1816;

    /* Spacing scale */
    --space-xs: 0.25rem;
    --space-sm: 0.5rem;
    --space-md: 1rem;
    --space-lg: 1.5rem;
    --space-xl: 2rem;
    --space-2xl: 3rem;
    --space-3xl: 4rem;
}

/* Smooth scrolling */
html {
    scroll-behavior: smooth;
}

/* Improved selection colors */
::selection {
    background: var(--desert-accent);
    color: var(--desert-shadow);
}

::-moz-selection {
    background: var(--desert-accent);
    color: var(--desert-shadow);
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Crimson Text', Georgia, serif;
    font-size: 18px;
    line-height: 1.7;
    color: var(--text-body);
    background: var(--desert-sand);
    min-height: 100vh;
}

/* Subtle desert texture overlay */
body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)'/%3E%3C/svg%3E");
    opacity: 0.03;
    pointer-events: none;
    z-index: -1;
}

/* Header & Navigation - Main site header only */
body > header {
    border-bottom: 1px solid var(--desert-border);
    padding: 0.5rem 2rem;
    position: sticky;
    top: 0;
    z-index: 100;
    background: var(--desert-sand);
}

/* Essay headers should not be sticky */
header.essay-header, .work-header {
    position: relative;
    border-bottom: none;
    padding: 0;
    background: transparent;
}

nav {
    max-width: 1100px;
    margin: 0 auto;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

a.logo {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    text-decoration: none;
}


