/* ==========================================================================
   Desert Syntax Highlighting - For Technical Essays
   Custom theme matching ClaudDib's desert aesthetic
   ========================================================================== */

/* Base code block styling */
pre {
    background: #0d0c0a;
    color: #fffef5;
    padding: 1.25rem;
    border-radius: 4px;
    overflow-x: auto;
    font-family: 'JetBrains Mono', 'Fira Code', 'SF Mono', Consolas, monospace;
    font-size: 0.85rem;
    line-height: 1.6;
    margin: 1.5rem 0;
    border-left: 3px solid var(--desert-accent);
}

code {
    font-family: 'JetBrains Mono', 'Fira Code', 'SF Mono', Consolas, monospace;
    font-size: 0.9em;
}

/* Inline code */
:not(pre) > code {
    background: rgba(201, 162, 39, 0.15);
    color: var(--desert-shadow);
    padding: 0.15rem 0.4rem;
    border-radius: 3px;
    border: 1px solid var(--desert-border);
}

/* Desert Theme Syntax Colors */
.token-comment,
.token.prolog,
.token.doctype,
.token.cdata {
    color: #b8b0a8; /* brightened muted */
    font-style: italic;
}

.token-punctuation {
    color: #d4cfc4;
}

.token-property,
.token-tag,
.token.boolean,
.token.number,
.token.constant,
.token.symbol,
.token.deleted {
    color: #ff6b5b; /* bright crustacean */
}

.token-selector,
.token.attr-name,
.token.string,
.token.char,
.token.builtin,
.token.inserted {
    color: #ffd147; /* bright gold */
}

.token-operator,
.token.entity,
.token.url,
.language-css .token.string,
.style .token.string {
    color: #d4cfc4;
}

.token-atrule,
.token.attr-value,
.token.keyword {
    color: #ffd700; /* bright amber */ /* light amber for keywords */
    font-weight: 500;
}

